mapper.select:
SELECT * 
FROM MEMBER 
WHERE MDELYN='N' AND MID=#{mid}

mapper.update:
UPDATE MEMBER 
SET MNAME=#{mname}, MALIAS=#{malias}, MEMAIL=#{memail}, MCP=#{mcp} 
WHERE MID=#{mid}

mapper.delete:
UPDATE MEMBER 
SET MDELYN='Y' 
WHERE MID=#{mid}

mapper.select:
SELECT COUNT(*) CNT 
FROM MEMBER 
WHERE MID=#{mid} AND MPASS=#{mpass}
